from flask import Flask, jsonify, request
from os.path import join, dirname
from deepface import DeepFace

# import request

app = Flask(__name__)

# Get the directory of the current script
current_dir = dirname(__file__)

@app.route('/verify/', methods=['POST'])
def verify_images():
    try:

        data = request.get_json()
        img1_req_data = data.get('img1_path')
        img2_req_data = data.get('img2_path')
    
        # Construct the absolute file paths
        # img1_path = r"data:image/jpeg;base64,"
        # img2_path = r"data:image/jpeg;base64,"
        img1_path = img1_req_data
        img2_path = img2_req_data
        # img2_path = r"data:image/jpeg;base64,"
        
        result = DeepFace.verify(img1_path=img1_path, img2_path=img2_path)
        
        return jsonify({'data': result, "success": True}), 200
    except Exception as e:
        return jsonify({'error': str(e), "success": False}), 500
    
@app.route('/test', methods=['GET'])
def test():
    try:        
        return jsonify({'data': "Success", "success": True}), 200
    except Exception as e:
        return jsonify({'error': str(e), "success": False}), 500


if __name__ == '__main__':
    app.run(host='0.0.0.0', port=105)
